void Accion_Tecla_Ast(void){//Realiza la función si se presionó la tecla *.
	 //Ingresa la frecuencia puesta en el LCD
	 if (Contador_Digito>=3){//Si ya se ingresaron los 3 digitos pone la nueva frecuencia.
		 OCR1A=Calcular_Tiempo_b(Digito1,Digito2,Digito3);//Calcula la frecuencia en base a una tabla de datos.
		 OCR1B=Calcular_Tiempo(Digito1,Digito2,Digito3);//Calcula la frecuencia realizando las operaciones matematicas.
		 POS_LINEA1(7);
		 ENVIA_DATO('*');//Muestra la tecla presionada.
		 Contador_Digito=0;//Resetea el contador de dígitos.
     }
}
